angular.module("dialogs.controllers", [ "ui.bootstrap.modal" ])
		.controller("errorDialogCtrl",["$scope","$modalInstance","msg",
		        function(o, a, l) {
									o.msg = angular.isDefined(l) ? l: "An unknown error has occurred.",
											o.close = function() {
												a.close();
											};
				} 
		])
		.controller("waitDialogCtrl",["$scope","$modalInstance","$timeout","msg","progress","m_title",
				function(o, a, l, n, i, t) {
						o.msg = angular.isDefined(n) ? n: "Attendi il completamento dell'operazione.",
						o.m_title = angular.isDefined(t) ? t: "Attendere Prego";
						o.progress = angular.isDefined(i) ? i: 100,
						o.$on("dialogs.wait.complete",
							function() {
								l(function() {
									a.close();
								});
							}),
						o.$on("dialogs.wait.message",
							function(a, l, t) {
								o.msg = angular.isDefined(l.msg) ? l.msg: o.msg;
							}),
						o.$on("dialogs.wait.progress",
							function(a, l, t) {
								o.msg = angular.isDefined(l.msg) ? l.msg: o.msg,
								o.progress = angular.isDefined(l.progress) ? l.progress: o.progress;
								o.m_title = angular.isDefined(t) ? t: o.m_title;
							}),
						o.getProgress = function() {
							return {
								width : o.progress + "%"
							};
						};
				}
		])
		.controller("notifyDialogCtrl",["$scope","$modalInstance","header","msg",
				function(o, a, l, n) {
						o.header = angular.isDefined(l) ? l: "Notification",
						o.msg = angular.isDefined(n) ? n: "Unknown application notification.",
						o.close = function() {
							a.close();
						};
				} 
		])
		.controller("loadDialogCtrl",["$scope","$modalInstance","header","msg",
				function(o, a, l, n) {
						o.header = angular.isDefined(l) ? l: "Loading",
						o.msg = angular.isDefined(n) ? n: "Loading",
						o.close = function() {
							a.close();
						};
				} 
		])
		.controller("confirmDialogCtrl",["$scope","$modalInstance","header","msg","btn1","btn2",
				function(o, a, l, n, y, d) {
						o.header = angular.isDefined(l) ? l: "Confirmation",
						o.msg = angular.isDefined(n) ? n: "Confirmation required.",
						o.btn1 = angular.isDefined(y) ? y: "Si",
						o.btn2 = angular.isDefined(d) ? d: "No",		
						o.no = function() {
							a.dismiss("no");
						},
						o.yes = function() {
							a.close("yes");
						};
				}
		]),
angular.module("dialogs.services",[ "ui.bootstrap.modal", "dialogs.controllers" ])
		.factory("$dialogs",["$modal",
				function(o) {
					return {
						error : function(a) {
							return o.open({
									templateUrl : "/dialogs/error.html",
									controller : "errorDialogCtrl",
									resolve : {
										msg : function() {
											return angular.copy(a);
										}
									}
							});
						},
						wait : function(a, l, t) {
							return o.open({
									templateUrl : "/dialogs/wait.html",
									controller : "waitDialogCtrl",
									resolve : {
										msg : function() {
											return angular.copy(a);
										},
										m_title : function() {
											return angular.copy(t);
										},
										progress : function() {
											return angular.copy(l);
										}
									}
							});
						},
						load : function(a, l) {
							return o.open({
									templateUrl : "/dialogs/load.html",
									controller : "loadDialogCtrl",
									resolve : {
										header : function() {
											return angular.copy(a);
										},
										msg : function() {
											return angular.copy(l);
										}
									}
							});
						},
						notify : function(a, l) {
							return o.open({
									templateUrl : "/dialogs/notify.html",
									controller : "notifyDialogCtrl",
									resolve : {
										header : function() {
											return angular.copy(a);
										},
										msg : function() {
											return angular.copy(l);
										}
									}
							});
						},
						confirm : function(a, l, y, d) {
							return o.open({
									templateUrl : "/dialogs/confirm.html",
									controller : "confirmDialogCtrl",
									resolve : {
										header : function() {
											return angular.copy(a);
										},
										msg : function() {
											return angular.copy(l);
										},
										btn1 : function() {
											return angular.copy(y);
										},
										btn2 : function() {
											return angular.copy(d);
										}
									}
							});
						},
						create : function(a, l, n, i) {
							var e = angular.isDefined(i.key) ? i.key: !0,
								s = angular.isDefined(i.back) ? i.back: !0;
							return o.open({
								templateUrl : a,
								controller : l,
								keyboard : e,
								backdrop : s,
								resolve : {
									data : function() {
										return angular.copy(n);
									}
								}
						});
					}
				};
		} 
]),
angular.module("dialogs", [ "dialogs.services", "ngSanitize" ])
	.run(["$templateCache",	function(o) {
		o.put(
			"/dialogs/error.html",
			'<div class="modal" id="errorModal" role="dialog" aria-Labelledby="errorModalLabel"><div class="modal-dialog"><div class="modal-content"><div class="modal-header dialog-header-error"><button type="button" class="close" ng-click="close()">&times;</button><h4 class="modal-title text-danger"><span class="glyphicon glyphicon-warning-sign"></span> Errore</h4></div><div class="modal-body text-danger" ng-bind-html="msg"></div><div class="modal-footer"><button type="button" class="btn btn-primary" ng-click="close()">Chiudi</button></div></div></div></div>'),
		o.put(
			"/dialogs/wait.html",
			'<div class="modal" id="waitModal" role="dialog" aria-Labelledby="waitModalLabel"><div class="modal-dialog"><div class="modal-content"><div class="modal-header dialog-header-wait"><h4 class="modal-title"><span class="glyphicon glyphicon-time"></span> {{m_title}}</h4></div><div class="modal-body"><p ng-bind-html="msg"></p><div class="progress progress-striped active"><div class="progress-bar progress-bar-info" ng-style="getProgress()"></div><span class="sr-only">{{progress}}% Complete</span></div></div></div></div></div>'),
		o.put(
			"/dialogs/notify.html",
			'<div class="modal" id="notifyModal" role="dialog" aria-Labelledby="notifyModalLabel"><div class="modal-dialog"><div class="modal-content"><div class="modal-header dialog-header-notify"><button type="button" class="close" ng-click="close()" class="pull-right">&times;</button><h4 class="modal-title text-info"><span class="glyphicon glyphicon-info-sign"></span> {{header}}</h4></div><div class="modal-body text-info" ng-bind-html="msg"></div><div class="modal-footer"><button type="button" class="btn btn-primary" ng-click="close()">OK</button></div></div></div></div>'),
		o.put(
			"/dialogs/load.html",
			'<div class="modal" id="loadModal" role="dialog" aria-Labelledby="loadModalLabel"><div class="modal-dialog"><div class="modal-content"><div class="modal-header dialog-header-wait"><h4 class="modal-title"><span class="glyphicon glyphicon-time"></span> Caricamento</h4></div><div class="modal-body"><circle-spinner></circle-spinner></div></div></div></div>'),
		o.put(
			"/dialogs/confirm.html",
			'<div class="modal" id="confirmModal" role="dialog" aria-Labelledby="confirmModalLabel"><div class="modal-dialog"><div class="modal-content"><div class="modal-header dialog-header-confirm"><button type="button" class="close" ng-click="no()">&times;</button><h4 class="modal-title"><span class="glyphicon glyphicon-check"></span> {{header}}</h4></div><div class="modal-body" ng-bind-html="msg"></div><div class="modal-footer"><button type="button" class="btn btn-default" ng-click="yes()">{{ btn1 }}</button><button type="button" class="btn btn-primary" ng-click="no()">{{ btn2 }}</button></div></div></div></div>');		
	}
]);